package Task6;

import java.util.Calendar;

public class Task6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
Calendar a=Calendar.getInstance();
System.out.println();
System.out.println("Year"+a.get(Calendar.YEAR));
System.out.println("Month"+a.get(Calendar.MONTH));
System.out.println("Date"+a.get(Calendar.DATE));
System.out.println("Day"+a.get(Calendar.DAY_OF_MONTH));
System.out.println("Hours"+a.get(Calendar.HOUR));
System.out.println("Minutes"+a.get(Calendar.MINUTE));
System.out.println("Seconds"+a.get(Calendar.SECOND));
	}

}
